        long long total = 0 ;
        for(int i = 0; i < arr.size() ; i++)
        {
            total += arr[i] ;
        }

        if(k == 1)
            return maxSum(arr, 1) ;
        if(k == 2 || total < 0){
            return max( maxSum(arr, 1), maxSum(arr, 2) ) % M ;
        }        
        return (maxSum(arr, 2) + total * (k-2)) % M ;

    }
};
