        int mod = 1e9 + 7;
        long long int currBest = -1e9, overallBest = -1e9;

        for(int i = 0 ; i < nums.size() ; i++){
            if(currBest + nums[i] >= nums[i]){
                currBest = (currBest + nums[i]%mod);
                if(currBest > overallBest){
                    overallBest = currBest;
                }
            }
            else{
                currBest = nums[i]%mod;
                if(currBest > overallBest){
                    overallBest = currBest;
                }   
            }
        }
        return overallBest%mod;
