// TLE: Heap
// Time: O(orders * log(inventoryLength)) | Given the constraint 'orders <= min(sum(inventory[i]), 10^9)', this time is bad since it is dependent on huge 'orders'
// Space: O(log(inventoryLength))
class Solution {
    public int maxProfit(int[] inventory, int orders) {

        PriorityQueue<Integer> pq = new PriorityQueue<>((a, b) -> inventory[b] - inventory[a]);
        for (int i = 0; i < inventory.length; ++i) {
            pq.add(i);
        }

        int profit = 0;
        while (orders-- > 0) {
            int removedIndex = pq.remove();
            profit += inventory[removedIndex];
            --inventory[removedIndex];
            pq.add(removedIndex);
        }
        return profit;
    }
}
