**Time Complexity-- O(N*logK)**
**N--size of inventory**
**K--max quantity of a ball in inventory**

```
class Solution {
public:

    //determiing the maximum quantity a coloured ball can have in the inventory
    bool canBeSold(int maxQty,vector<int> &inv,int orders)
    {
        int n = inv.size();
        for(int i=n-1;i>=0;i--)
        {
            //determining balls to be sold to achieve maxQty balls
            int ballsToBeSold = max(0,inv[i]-maxQty);
            //updating orders
            orders-=ballsToBeSold;
            //if we need to sell more balls than orders,,then we can't have a max of maxQty balls of a type in inventory
            if(orders<0)
                return false;
        }
        return true;
    }

    //calculating the profit we achieve
    /*
    formula:
    sum of n numbers = n*(n+1)/2
    sum of k numbers = k*(k+1)/2
    sum of n-k numbers = n*(n+1)/2 - k*(k+1)/2
    */
    int calcProfit(int low,vector<int> &inv,int orders)
    {
        int mod = 1e9+7;
        long long maxBallinInv = low;
        int ans=0;
        //future cost--cost for removing 'low' no. of balls
        long long futureCost = (maxBallinInv*(maxBallinInv+1))/2;

        for(int i=0;i<inv.size();i++)
        {
            if(inv[i]<=maxBallinInv)
                continue;
            long long balls = inv[i];
            //present cost-- cost for rmeoving 'inv[i]' no. of balls
            long long presentCost = (balls* (balls+1))/2;

            ans = (ans%mod + (int)((presentCost - futureCost)%mod))%mod;
            //updating orders
            orders -= (inv[i]-maxBallinInv);
        }
        //after achieving a maximum of 'low' balls in the inventory we process the remaining orders
        ans = (ans%mod + (orders*maxBallinInv)%mod)%mod;
        return ans%mod;        
    }

    int maxProfit(vector<int>& inv, int orders) 
    {
        sort(inv.begin(),inv.end());
        int low = 0,high = inv.back();

        //determing the max qty a coloured ball can have in the inventory
        while(low<high)
        {
            int mid = low + (high-low)/2;
            if(canBeSold(mid,inv,orders))
                high=mid;
            else
                low=mid+1;
        }
        return calcProfit(low,inv,orders);
    }
};
```

