class Solution
{
    public:
        int maxProfit(vector<int> &inv, int ord)
        {
            map<long, long> m;
            for (int i = 0; i < inv.size(); i++) m[inv[i]] += 1;
            m[0] = 0;
            priority_queue<pair<long, long>, vector< pair<long, long>>> heap;
            for (auto i: m) heap.push(i);
            long long cost = 0;
            while (ord)
            {
                auto x = heap.top();
                heap.pop();
                auto y = heap.top();
                heap.pop();
                long balls = (x.first - y.first) *x.second;
                if (balls <= ord)
                {
                    long long n1 = (x.first *(x.first + 1)) / 2;
                    long long n2 = (y.first *(y.first + 1)) / 2;
                    ord -= balls;
                    cost += (n1 - n2) *x.second;
                }
                else
                {
                    long req = ord / x.second;
                    long long n1 = (x.first *(x.first + 1)) / 2;
                    long long n2 = ((x.first - req) *((x.first - req) + 1)) / 2;
                    cost += (n1 - n2) *x.second;
                    x.first -= req;
                    req = ord % x.second;
                    cost += x.first * req;
                    return cost % 1000000007;
                }
                y.second += x.second;
                heap.push(y);
            }
            return cost % 1000000007;
        }
};
