class Solution {
    private class Pair {
        long p;
        int cnt;

        Pair(long p, int cnt) {
            this.p = p;
            this.cnt = cnt;
        }
    }
    public int maxProfit(int[] inventory, int orders) {
        PriorityQueue<Pair> pq = new PriorityQueue<>((a, b) -> Long.compare(b.p, a.p));
        for (int cnt : inventory) {
            pq.offer(new Pair(cnt, 1));
        }
        long profit = 0;
        while (orders > 0 && !pq.isEmpty()) {
            Pair p = pq.poll();
            while (!pq.isEmpty() && pq.peek().p == p.p) {
                p.cnt += pq.poll().cnt;
            }
            long diff = pq.isEmpty() ? p.p : p.p - pq.peek().p;
            if (p.cnt * diff <= orders) {
                orders -= p.cnt * diff;
                profit += p.cnt * (p.p - diff + 1 + p.p) * diff / 2;
                if (p.p - diff > 0) {
                    pq.offer(new Pair(p.p - diff, p.cnt));
                }
            } else {
                int len = orders / p.cnt;
                profit += len * (p.p - len + 1 + p.p) * p.cnt / 2;
                profit += (orders % p.cnt) * (p.p - len);
                orders = 0;
            }
        }
        return (int) (profit % 1000000007);
    }
}
