var maxProfit = function (A, t) {
  const m = BigInt(1e9 + 7),
    N = A.length;
  let l = 1,
    r = Math.max(...A);

  // Find a price l that all other prices p whre p >= l, count up to t
  while (l < r) {
    const mid = (l + r) >> 1;
    let s = 0;
    for (let i = 0; i < N; i++) if (A[i] >= mid) s += A[i] - mid + 1;
    // Not enough, decrease mid
    if (s <= t) r = mid;
    // Too much, increase mid
    else l = mid + 1;
  }

  // Convert to BigInt
  t = BigInt(t);
  let res = 0n;
  for (let i = 0; i < N; i++) {
    if (A[i] >= l) {
      const small = BigInt(l),
        large = BigInt(A[i]),
        count = BigInt(A[i] - l + 1);
      res += (((small + large) * count) / 2n) % m;
      res %= m;
      t -= count;
    }
  }
  res += (t * BigInt(l - 1)) % m;
  return res % m;
};
