class Solution {
   int mod=1000000007;
    public int maxProfit(int[] inventory, int orders) {
        long ans=0;
        Arrays.sort(inventory);
		//it is used to count the window
        long factor=1;
        int i=inventory.length-1;
        while(orders>0)
        {
			//cases like [5,6,7,9,9,9] and order is 35
            if(i>0 && inventory[i]>inventory[i-1] && orders>=factor*(inventory[i]-inventory[i-1]))
            {
                ans+=factor*(getSum(inventory[i],inventory[i-1]));
                ans=ans%mod;
                orders-=factor*(inventory[i]-inventory[i-1]);
            } // cases like [8,8,8] but orders is 10 
			else if(i==0 || inventory[i]>inventory[i-1])
            {
			   //use long to avoid overflow
                long sellItems=(orders/factor);
                ans+=factor*getSum(inventory[i],inventory[i]-sellItems);
                ans=ans%mod;
                long remain=(orders%factor);
                ans+=(inventory[i]-sellItems)*remain;
                ans=ans%mod;
                break;
            }
            factor++;
            i--;
        }
        return  (int)ans;
    }
    // to get the sum of numbers in the window
    public long getSum(long left, long right)
    {
        left=left%mod;
        right=right%mod;
        return ((left*(left+1))/2)-(right*(right+1)/2);
    }
}
