public class Solution {
    public static int maxProfit(int[] inventory, int orders) {
        // sort all items
        Arrays.sort(inventory);

        // insert 0 to the head of array;
        int len = inventory.length+1;
        int[] item = new int[len];
        item[0] = 0;
        System.arraycopy(inventory, 0, item, 1, len - 1);

        /**
         * start from the biggest e.g. [2,4,6,8,10], 26
         * num=1 loop=offset=2, remain=0, ans += (10+9) + 0 = 19, order = 22-2 = 20, items = [0,2,4,6,8,8]
         * num=2 loop=offset=2, remain=0, ans += 2*(8+7) + 0 = 49, order = 20-2*2 = 16, items = [0,2,4,6,6,6]
         * num=3 loop=offset=2, remain=0, ans += 3*(6+5) + 0 = 82, order = 16-3*2 = 10, items = [0,2,4,4,4,4]
         * num=4 loop=offset=2, remain=0, ans += 4*(4+3) + 0 = 110, order = 10-4*2 = 2, items = [0,2,2,2,2,2]
         * num=5 loop=6/5=1 < offset=2, remain=6%5=1 ans += 5*2 + 1*1 = 121, order = 2-1*1-1 = 0, items = [0,1,1,1,1,0]
         */
        long ans = 0;
        int count = len-1; // start from the biggest value
        while(orders > 0) {
            // number of elements with same value
            int num = len - count;
            // number of loops current value needed
            long loop = orders/num;
            // remain orders after loop
            long remain = orders%num;

            // the offset between current value and next value
            long offset = item[count] - item[count - 1];

            // If the current value is reduced to the same as the next value, end the loop
            if(loop >= offset) {
                loop = offset;
                remain = 0;
            }

            // add loop value and remain value
            ans += num*(item[count]+item[count]-loop+1)*loop/2 + remain*(item[count]-loop);
            orders -= loop*num+remain;

            count--;
        }

        return (int)(ans%1000000007);
    }
}

