class Solution {
public:

    long long getSum(long long num)
    {return num * (num+1) / 2;}

    int maxProfit(vector<int>& inventory, int orders) {
        map<int, int> mp;
        //get count of each element
        for(int i : inventory)
            mp[i]++;

        //Iterate count from behind
        auto itr = mp.rbegin();
        const int MOD = 1e9+7;
        long long res = 0;

        //Iterate as long as we have orders remaining.
        while(orders > 0)
        {
            //Get the current element and its count.
            long long curr = itr->first, count = itr->second;

            //Move iterator by 1 and get next element's value
            itr = next(itr, 1);
            long long next = itr != mp.rend() ? itr->first : 0;

            //Calculate total number of balls that you can fill.
            long long totalBallsAvailable = count * (curr - next);

            //Order can consume all the available balls
            if(orders >= totalBallsAvailable)
            {
                res = (res + count * (getSum(curr) - getSum(next))) % MOD;

                //Add the current count to next element and deduct the number of balls filled from order.
                itr->second += count;
                orders -= totalBallsAvailable;
            }
            //Else we have more balls than we need.
            else
            {
                //Calculate number of orders current count can fill and remaining orders after we fill with current count.
                long long numOfOrdersCurrCanFulfill = orders/count, remainingOrders = orders % count;

                //Fill the orders with current count
                res = (res + (count * ( getSum(curr) - getSum(curr - numOfOrdersCurrCanFulfill) ) ) ) % MOD;
                //Fill the orders with remaining balls.
                res = (res + (remainingOrders * (curr - numOfOrdersCurrCanFulfill) ) ) % MOD;

                orders = 0;
            }
        }
        return res;
    }
};
