    int maxProfit(vector<int>& inventory, int orders) {
        int n = inventory.size();
        std::map<int, int> star; // key: ball counts of each color, value: count of color
        for(int i = 0; i < n; ++i) {
            ++star[inventory[i]];
        }

        long long res = 0;
        int mod = 1e9 + 7;
        int bought = 0;

        while(bought < orders) {

			// for each fetch round, we only fetch the most count color balls, the last element in map
            std::map<int,int>::reverse_iterator rit = star.rbegin();
            long long upper = rit->first; // the current most ball count (could be more than one color)
            if (upper <= 0) break;
            ++rit;
            long long low = (rit == star.rend() ? 0 : rit->first); // the current second most ball count

            int cnt = star[upper];
            int collect = upper - low; // for each color ball, we can only fetch this number, then the most will become second most. we can't fetch more, need wait next iteration
            int total = collect * cnt;
            if (total + bought >= orders) { // if we can finish in this iteration
                int equallyfetch = (orders - bought) / cnt; // fetch this from all buffer
                int leftover = (orders - bought) % cnt;

                res += cnt * ((upper + upper - equallyfetch + 1) * equallyfetch / 2);
                res += leftover * (upper - equallyfetch);

                bought = orders;
                break;

            } else {
                res += cnt * ((upper + low + 1) * collect / 2);
                bought += collect * cnt;

                star[low] += cnt;
                star[upper] = 0;
                star.erase(upper);
            }

            res %= mod;

        }
        return res % mod;

    }

