  public int maxProfit(int[] nums, int order) {
        if(nums == null || nums.length == 0 || order == 0) return 0;
        long sum = 0;
        TreeMap<Integer, Integer> map = new TreeMap<>((a, b) -> (b - a));
        for(int num : nums){
            map.put(num, map.getOrDefault(num, 0) + 1);
        }
        int mod = 1000000007;

        while(order > 0){
           int curMax = map.firstKey();
           int freq = map.get(curMax);
           Integer second = map.ceilingKey(curMax - 1);
           if(second == null) second = 0;

           int diff = curMax - second;

           if(diff * freq <= order){
               order -= diff * freq;
               sum += ((sumFromStartToEnd(second + 1, curMax) % mod)  * freq) % mod;
               sum = sum % mod;
               if(second == 0) return (int)sum;
               map.put(second, map.get(second) + freq);
               map.remove(curMax);
           }else{              
               diff = order / freq;
               long res = order % freq;
               sum += ((sumFromStartToEnd(curMax - diff + 1, curMax) % mod) * freq) % mod;
               sum += (res * (curMax - diff)) % mod;
               return (int)sum;
           }
        }
        return (int)sum;
    }

    public long sumFromStartToEnd(long start, long end){
        return (end + start) * (end - start + 1)/2;
    }
