def maxProfit(self, inventory: List[int], orders: int) -> int:
	inventory.sort()
	i, j = 1, inventory[-1]
	ans = -1
	def isValid(v):
		t = orders
		m = bisect_left(inventory, v)
		for i in range(m, len(inventory)):
			t -= inventory[i] - v
			if(t <= 0):
				return 1
		if(t <= len(inventory)-m):
			return 1
		return 0
	while(i <= j):
		mid = i + (j-i) // 2
		if(isValid(mid)):
			ans = mid
			i = mid+1
		else:
			j = mid-1
	if(ans == -1):
		return -1
	ans, v = 0, ans
	for i in range(len(inventory)):
		if(inventory[i] > v):
			ans = (ans + ((inventory[i]+1)*inventory[i])//2 - ((v+1)*v)//2) % 1000000007
			orders -= inventory[i] - v
	if(orders):
		ans = (ans + v * orders) % 1000000007
	return ans
