class Solution {
public:
    int func(long long n, long long m, int cnt){
        int mod=1e9+7;
        long long x=(n*(n+1)/2);
        long long y=(m*(m+1)/2);
        //cout<<n<<" "<<m<<" "<<cnt<<" "<<x-y<<endl;
        return ((x-y)*cnt)%mod;
    }

    int maxProfit(vector<int>& nums, int orders) {
        int n=nums.size();
        long long cost=0;
        priority_queue<pair<long long,long long>> pq;
        sort(nums.begin(),nums.end());
        int mod=1e9+7;
        long long prev=nums[0],cnt=1;
        for(int i=1;i<n;i++){
            if(prev==nums[i]){
                cnt++;
            }else{
                pq.push({prev,cnt});
                prev=nums[i];
                cnt=1;
            }
        }

        pq.push({prev,cnt});
        pq.push({0,0});

        while(pq.size()>1){
            pair<long,long> top=pq.top();
            pq.pop();
            pair<long,long> second_top=pq.top();
            pq.pop();
            long long cnt=(long long)(top.first-second_top.first)*top.second;
            if(cnt>=orders){
                long long x=orders/top.second,y=orders%top.second;
                if(x!=0){
                    //cout<<cost<<endl;
                    cost+=func(top.first,top.first-x,top.second)%mod;
                    //cout<<cost<<endl;
                    if(y!=0)
                        cost+=((top.first-x)*y)%mod;
                }else{
                    cost+=(top.first*y)%mod;
                }
                return cost%mod;
            }else{
                cost+=func(top.first,second_top.first,top.second)%mod;
                orders-=cnt;
                pq.push({second_top.first,top.second+second_top.second});
            }
        }

        return cost%mod;
    }
};
