    int maxProfit(vector<int>& inventory, int orders) {
        const int modulo = 1000000007;

        int total = 0;

		// descending order
        sort(inventory.begin(), inventory.end(), std::greater<int>());
        for (int i = 0; i < inventory.size();) {

            int columns = i + 1;
            // find next column w less elements so we can consume whole rows
            while (columns < inventory.size() && inventory[columns] == inventory[i]) {
                ++columns;
            }

            uint64_t current = inventory[i];
            uint64_t next = (columns < inventory.size()) ? inventory[columns] : 0;

            // number of available elements
            int available = (current - next) * columns;
            // actual required count which could be less than available
            int count = std::min(available, orders);

            // number of fully consumed rows
            int rows = count / columns;
            // number of elements not equal to row cnt, it means we will be full
            int leftover  = count % columns;

            // sum of element in each column: (Am + An) * (m - n + 1) / 2
            // total sum =  sum of column * colunbs
            uint64_t sum = (current + (current - rows + 1)) * rows / 2 * columns;
            if (leftover){
                // if there unequal number of elements left
                // then simply number of columns left * value of the row
                sum += (current - rows) * leftover;
            }

            total = (total + sum) % modulo;
            orders -= count;
            if (!orders) {
                break;
            }

            i = columns;
        }

        return total;
    }
