class Solution {
public:
    long long int sum(int i) {
        //return sum of first i natural numbers;
        return ((long long) i * (i + 1)) / 2;
    }

    int maxProfit(vector<int>& inventory, int orders) {
        int n = (int) inventory.size();
        sort(inventory.rbegin(), inventory.rend());
        inventory.push_back(0); n++;
        long long cnt = 1, ans = 0, MOD = 1e9 + 7;
        for (int i = 1; i < n; i++) {
            if (inventory[i] == inventory[i - 1]) {
                cnt++;
            } else {
                long long total = (inventory[i - 1] - inventory[i]) * cnt;
                if (orders >= total) {
                    ans += (((sum(inventory[i - 1]) -  sum(inventory[i])) % MOD) * cnt);
                    ans %= MOD;
                    orders -= total;
                } else {
                    int take = orders / cnt;
                    ans += (((sum(inventory[i - 1]) - sum(inventory[i - 1] - take)) % MOD) * cnt);
                    long long rem = orders % cnt;
                    ans += (rem * (inventory[i - 1] - take));
                    ans %= MOD;
                    break;
                }
                cnt++;
            }
        }
        return ans;
    }
};
