class Solution {
public:
    const int mod=1e9+7;
    int maxProfit(vector<int>& inventory, int orders) {

        long res=0,prev=0,curr,color=1;
        sort(inventory.begin(),inventory.end());
        for(int i=inventory.size()-1;i>=0&&orders>0;i--,color++){

            curr=inventory[i],prev = i>0?inventory[i-1]:0;
            long rounds=min(orders/color,curr-prev);
            orders-=(rounds*color);
               res = (res + (curr * (curr + 1) - (curr - rounds) * (curr - rounds + 1)) / 2 * color) % 1000000007;

            if(curr-prev>rounds){
                res=(res+(curr-rounds)*orders)%mod;
                break;
            }
        }
        return res;
    }
};
