class Solution {

public:
    int modu = pow(10,9)+7;
    int maxProfit(vector<int>& inventory, int orders) {
        priority_queue<long> pq;
        for( int i = 0 ; i< inventory.size() ; i++ ){
            pq.push( inventory[i] );
        }
        long rem = orders;
        long amount = 0;
        while( !pq.empty() || rem > 0 ){
            long a = pq.top(); pq.pop();
            if( pq.empty() ){
                amount += ( a*(a+1)/2 - (a-rem)*(a-rem+1)/2 );
                amount = amount%modu;
