 //Assuming a list 123,123,134,134,134 you don't have to take one at a time
    //from each of 134. You know you have 33 availability(134-123)*3 till you 
    //get 5 sets of 123 balls. So if your order is greater than 
    //33 balls you can get to 5 sets of 123 balls each in one step and repeat 
    //the process with 5 sets of 123 balls each. But if your orderRemaining is 
    //less than 33,  or you have sets of same number of balls remaining, i.e. 
    //5 sets of 123 each for example, then you can just get to final answer in 
    //one step. You just have to split the order carefully. For example, if 
    //you need only 23 then you can get 7(23/3) from each set and 1 each from 
    //two sets. So the final count will be 123,123,126,126,127. There is a 
    //special case here if you only needed 21 or 2. For 21, you just take 7 
    //from each set so the second step of taking 2 should not be done but for 
    //2 you only take 1 from 2 sets each so the first step of taking 7 from 
    //each should not be done. 
    //The other solutions of using priority queue doe not working because
    //you go one ball at a time.

    int maxProfit(vector<int>& inventory, int orders) {
        map<int,int> mp;
        for( auto inv: inventory)
            mp[inv]+= 1;

        int orderFilled = 0;
        long int sum = 0;
        int mod = 1000000007;
        while(orderFilled<orders)
        {
            int availability;
            auto start = mp.rbegin();
            auto end = start;

            while((end != mp.rend() ) && (end->first == start->first))
                end++;

            long int topValue = start->first;
            long int topValue2;
            //take what you need from start 
            if( end == mp.rend())
            {
                int orderRemaining = orders-orderFilled;
                return takewhatever(mp,start,orderRemaining,
                                    topValue, sum,mod);
            }
            else
            {
                topValue2 = end->first;
                int availability = (topValue-topValue2)*start->second;
                int orderRemaining = orders-orderFilled;
                if( orderRemaining > availability)
                {
                    orderFilled += availability;
                    mp[topValue2] += mp[start->first];
                    mp.erase(start->first);
                    sum = (sum+((topValue+topValue2+1)*
                                availability/2)%mod)%mod;
                }
                else
                {
                    return takewhatever(mp,start,orderRemaining,
                                        topValue, sum,mod);
                }   
            }

        }
        return sum;
    }

    double takewhatever(map<int,int>& mp, 
                        map<int,int>::reverse_iterator start, 
                        int orderRemaining, long int topValue, 
                        long int sum,int mod )
    {
        int howmanyduplicates = start->second;
        int howmanytotakefromeach = 
                orderRemaining/howmanyduplicates;
        int howmanyleft = orderRemaining-            
            (howmanytotakefromeach*howmanyduplicates);
        mp.erase(start->first);
        if(howmanyleft)
        {
            int splitAmount1 = topValue-howmanytotakefromeach;
            int count1 = howmanyduplicates-howmanyleft;
            int splitAmount2 = splitAmount1-1;
            int count2 = howmanyleft;
            if(howmanytotakefromeach)
                sum = (sum+((topValue+splitAmount1+1)*
                    (topValue-splitAmount1)*count1/2)%mod)%mod;
            sum = (sum+((topValue+splitAmount2+1)*
                (topValue-splitAmount2)*count2/2)%mod)%mod;
        }
        else
        {    
            int splitAmount1 = topValue-howmanytotakefromeach;
            int count1 = howmanyduplicates;
            sum = (sum+((topValue+splitAmount1+1)*
                    (topValue-splitAmount1)*count1/2)%mod)%mod;
        }

        return sum;
    }
