class Solution {
public:
    int maxProfit(vector<int>& inventory, int orders) {
        int mod = 1e9 + 7;
        sort(inventory.begin(), inventory.end(), greater());
        inventory.push_back(0);
        int64_t res = 0;
        for(int i = 1; i < inventory.size(); ++i){
            if(orders == 0) break;
            if(orders >= i * (inventory[i-1] - inventory[i])){
                orders -= i * (inventory[i-1] - inventory[i]);
                res += (int64_t)i * (inventory[i-1] + inventory[i] + 1) * (inventory[i-1] - inventory[i]) / 2;
                res %= mod;
            }
            else{
                int64_t reminder = orders % i;
                orders /= i;
                res += (int64_t)i * (inventory[i-1] + inventory[i-1] - orders + 1) * orders / 2;
                res %= mod;
                res += reminder * (inventory[i-1] - orders);
                res %= mod;
                return res;
            }
        }
        return res;
    }
};
