        for(int j = inventory.length - 1; j >= 0; j--){
            if(inventory[j] < threshold) break;
            profit = (profit%mod + (getSum(inventory[j], orders, threshold)%mod)) % mod;
            orders -= Math.min(orders, inventory[j] - threshold);
            if(orders == 0) break;
        }
        if(orders != 0) return arr;
        arr[0] = 1; arr[1] = profit;
        return arr;
    }

    private long getSum(int available, int orders, int threshold){
        int mod = (int)1e9+7;
        long x = (long)available;
        long b = x * (x+1)/2;
        long reduceTo = Math.max(available - orders, threshold);
        long a = reduceTo * (reduceTo+1)/2;
        return (b%mod-a%mod) % mod;
    }
