class Solution {
    public int maxProfit(int[] nums, int orders) {
        long low = 0, high = 1_000_000_000, order = orders;
        while(low < high) {
            long count = 0, mid = low + (high - low) / 2;
            for(int num : nums)
                count += Math.max(0, num - mid);
            if(count > order)
                low = mid + 1;
            else
                high = mid;
        }

        long result = 0;
        for(int num : nums) {
            if(num > low) {
                long n = num, l = low;
                result += (n * (n + 1) / 2) - (l * (l + 1) / 2);
                result = result % 1_000_000_007;
                order -= (num - low);
            }
        }

        return (int)((result + low * order) % 1_000_000_007);
    }
}
