class Solution {
    public int maxProfit(int[] inventory, int orders) {
        inventory = Arrays.stream(inventory).boxed()
        .sorted(Collections.reverseOrder())
        .mapToInt(Integer::intValue)
        .toArray();
        long sum = 0;
        int top=inventory[0],j=0,n=0,nexttop=0;

        while(orders>0){
            while(j+1<inventory.length && top==inventory[j+1]){j++;}
            n=j+1;
            if(j==inventory.length-1){nexttop=0;}
            else{nexttop=inventory[j+1];}
            int diff = top-nexttop;
            long dec = (long)n*diff;
            if(dec<=orders){
                sum= (long)sum+n*(sumFromNtoX(top,nexttop));
                orders-=dec;
            }
            else{
                long div = orders/n;
                long rem = orders%n;

                sum= (long)sum+n*(sumFromNtoX(top,top-div));
                sum= (long)sum+rem*(top-div);

                orders=0;
            }
            top=nexttop;
            sum=(long)sum%(1000000007);
        }
        return (int)(sum%(1000000007));
    }

    long sumFromNtoX(long n, long x){
        long ret = (long)(n * (n+1))/2 - (x * (x+1))/2;
        return ret;
    }
}
```

