func maxProfit(inventory []int, orders int) int {
	sort.Slice(inventory, func(i, j int) bool {
		return inventory[i] > inventory[j]
	})

	n := len(inventory)
	total := 0
	remaining := orders
	index := 0

	for index < n {
		curr := inventory[index]
		next := 0

		if index < n-1 {
			next = inventory[index+1]
		}

		count := (curr - next)
		diff := count * (index + 1)
		correction := false

		if diff > remaining {
			correction = true
			count = remaining / (index + 1)
		}

		total += ((curr * 2 + 1 - count) * count / 2) * (index + 1)
		remaining -= (index + 1) * count
		curr -= count

		if correction {
			total += (curr * remaining)
			remaining = 0
		}

		index += 1
		total %= 1000000007
	}

	return total
}
```

