```java
// O (N log M) N is the length of inventory, M is the max value in inventory.
class Solution {
	private final static int MOD = 1_000_000_007;

	public int maxProfit(int[] inventory, int orders) {
		int len = inventory.length;
		if (len == 1) {
			long temp = (orders)*((long)inventory[0] * 2 - orders + 1) / 2;
			return (int)(temp % MOD);
		}
		int maxN = Integer.MIN_VALUE;
		for (int i : inventory) {
			if (i > maxN) maxN = i;
		}

		// binary search
		int start = 1; int end = maxN + 1;
		while (start < end) {
			int mid = start + (end - start) / 2;
			long temp = getOrders(inventory, mid);
			if (temp == orders) {
				start = mid;
				break;
			} else if (temp < orders) {
				end = mid;
			} else if (temp > orders) {
				start = mid + 1;
			}
		}

		long res = 0;
		for (int i : inventory) { 
			if (i >= start) {
				res += ((long)start + i)*(i-start+1)/2;
				res %= MOD;
			}
		}
		res += (orders - getOrders(inventory, start)) * (start - 1);
		res %= MOD;

		return (int)res;
	}

	private long getOrders(int[] inventory, int n) {
		long ret = 0;
		for (int i : inventory) {
			if (i >= n) {
				ret += i - n + 1;
			}
		}
		return ret;
	}
}
