                int count = Math.Min(inventory[i] - left, orders);//count is of range [left+1,inventory[i] ]
                res += (a + (a - count + 1)) * count / 2;
                res %= mod;
                orders -= count;
            }

            res += orders % mod * left;//must % mod, or cause overflow
            return (int)(res % mod);
