var maxProfit = function (inventory, orders) {
  orders = BigInt(orders);
  let ans = BigInt(0);
  let count = BigInt(1);
  const maxHeap = new MaxHeap(inventory);

  while (orders > 0 && maxHeap.getLength() > 0) {
    const curr = BigInt(maxHeap.remove());
    let next = maxHeap.getLength() > 0 ? BigInt(maxHeap.peek()) : BigInt(0);
    const createdOrders = (curr - next) * count;

    // we can take all
    if (createdOrders <= orders) {
      orders -= createdOrders;
      const sum = mod((getSum(curr) - getSum(next)) * count);
      ans = mod(ans + sum);
    } else {
      // we can take only partially
      next = curr - orders / count;
      const sum = mod((getSum(curr) - getSum(next)) * count);
      ans = mod(ans + sum);

      // for the remaining orders, we can just pick the next.
      const remaining = orders % count;
      ans = mod(ans + remaining * next);
      orders = 0;
    }
    count++;
  }

  return ans;
};

