class Solution {
public:
    int maxProfit(vector<int>& inventory, int orders) {
        const int MOD = 1000000007;
        int ans = 0;

		// Sort balls by descending count order
        sort(inventory.begin(), inventory.end(), std::greater<int>());

        // Used to box group of colours that should be sold next
        int leftBoundary = 1;

        // Keep counting profit until orders are fulfilled
        while (orders > 0) {
            // Find next colour with less balls
            while (leftBoundary < inventory.size() && inventory[leftBoundary] == inventory[leftBoundary - 1]) {
                ++leftBoundary;
            }
            long currentCount = inventory[leftBoundary - 1];
            long nextCount = (leftBoundary < inventory.size()) ? inventory[leftBoundary] : 0;
            // At this point all colours between indexdes 0 and leftBoundary - 1 inclusively have the same highest count (currentCount - nextCount)

            // Determine number of balls that could potentially be sold
            int potentialSellCount = (currentCount - nextCount) * leftBoundary;

            // Determine actual number of balls that can be sold (don't sell more than the order)
            int sellCount = std::min(potentialSellCount, orders);

            // Number of balls to take from each colour
            int ballsPerColour = sellCount / leftBoundary;
            // Update number of remaining balls (we should technically update everything between 0 and leftBoundary but it would be a waste of time)
            //std::fill_n(inventory.begin(), leftBoundary, currentCount - ballsPerColour);
            inventory[leftBoundary - 1] -= ballsPerColour;

            // When orders < potentialSellCount, we won't consume all the colours with max counts. Some leftover orders will have to be fulfilled
            int rem  = sellCount % leftBoundary;

            long sumPerColumn = nSum(currentCount) - nSum(currentCount - ballsPerColour);
            long profit = sumPerColumn * leftBoundary;
            if (rem > 0){
                // When orders < potentialSellCount. Take care of leftover orders to fulfill
                int nextValue = currentCount - ballsPerColour;
                profit += (long)nextValue * rem;
            }

            ans = (ans + profit) % MOD;
            orders -= sellCount;
        }

        return ans;
    }

private:
    long nSum(long n) {
        // Computes n + (n - 1) + (n - 2) + ... + 1
        // Gauss formula: https://nrich.maths.org/2478#:~:text=Gauss%20added%20the%20rows%20pairwise,quantity%20in%20a%20clever%20way.
        return n*(n+1)/2;
    }
};
