struct Solution {
    static const auto mod = 1'000'000'007;

    bool check(int mid, int orders, vector<int>& arr) {
	/* Determine if buying all balls of value >= mid yields at least orders orders */
        int res = 0;
        for(auto t : arr) {
            res += (t >= mid) ? t-mid + 1 : 0; /* buy all balls of value >= mid */

            if (res >= orders) /* avoid overflow */
                break;
        }
        return res >= orders;
    }

    int maxProfit(vector<int>& arr, int orders) {
	/* maximum profit given orders orders */
        int l = 0, r = 1'000'000'000;

        while(l < r) {
            int mid = (l + r + 1) / 2;
            if (check(mid, orders, arr)) l = mid;
            else r = mid - 1;
        } /* l == r */

        int64_t ans = 0; /* return value */

        for(auto t : arr) {
            if (orders == 0) break;
            if(t > l) {
                int64_t cnt; 
                orders -= cnt = min(orders, t - l); /* buy all balls of value strictly greater than l */
                ans += cnt * (t+t-cnt+1)/2; /* add up the values of the balls bought (sum of arithmetic series) */
                ans %= mod;
            } 
        }
        if(orders) /* if any orders left, by some balls of value l */
            ans = (ans + (int64_t) orders * l) % mod;
        return ans;
    }
};
