class Solution:
  def __init__(self):
    self.MOD = 10**9+7

  def getProfit(self, n1, n2):
    p1 = (n1*(n1+1)//2) % self.MOD 
    p2 = (n2*(n2+1)//2) % self.MOD 
    return (p1-p2) % self.MOD 

  def maxProfit(self, inventory: List[int], orders: int) -> int:
    profit = 0
    lo = 0
    hi = max(inventory) + 1
    while lo < hi:
      mid = lo + (hi-lo)//2
      s = 0
      for num in inventory:
        if num > mid:
          s += num - mid
      if s > orders:
        lo = mid + 1
      else:
        hi = mid
    for num in inventory:
      if num <= hi:
        continue
      profit = (profit + self.getProfit(num, hi)) % self.MOD
      orders -= num - hi
    for num in inventory:
      if orders == 0:
        break
      if num >= hi:
        profit = (profit+hi) % self.MOD
        orders -= 1
    return profit
