class Solution {
public:
    int maxProfit(vector<int>& inventory, int orders) {

        // inventory array:
        // ... large elements (>= last_choice) ... |  ...unsettled elements... | ... small elements (ignored) ...
        //                                         ^                           ^
        //                                       first                        last
        int64_t first = 0, last = inventory.size();
        int64_t last_choice = INT_MAX;
        int64_t target_orders = orders;

        do
        {
            if(last - first == 1)
            {
                // find x, s.t. first * (last_choice - x) + (inventory[first] - x) >= target_orders
                last_choice = (first * last_choice + inventory[first] - target_orders) / (first + 1);
                break;
            }

            int64_t mid = (last + first) / 2;

            nth_element(inventory.begin() + first, inventory.begin() + mid, 
                        inventory.begin() + last, greater<int>());

            int64_t trial_orders = accumulate(inventory.begin() + first, inventory.begin() + mid, 0L)
                                   - inventory[mid] * mid + first * last_choice;

            if(trial_orders > target_orders)
            {
                last = mid;
            }
            else
            {
                target_orders -= trial_orders;
                last_choice = inventory[mid];
                first = mid;
            }

        }while(target_orders > 0);

        int64_t total_profit = 0;
        int64_t total_orders = 0;
        for(int i = 0; i <= first; ++i)
        {
            int64_t norders = inventory[i] - last_choice;
            total_orders += norders;
            total_profit += (inventory[i] + last_choice + 1) * norders / 2;
        }

        int64_t overfulfilled_orders = total_orders - orders;
        if(overfulfilled_orders > 0)
        {
            total_profit -= (last_choice + 1) * overfulfilled_orders;
        }

        return total_profit % 1000000007;
    }
};
