    let totalCost = 0;
    let fulfilledOrders = orders

    while (fulfilledOrders > 0) {
        inventory.sort((a, b)=>b-a)
        if (inventory[0] > inventory[1]) {
            const ordersCanFulfill = inventory[0] - inventory[1]

            const newFufilledOrders = Math.min(fulfilledOrders, ordersCanFulfill)
            fulfilledOrders -= newFufilledOrders

            const cost = getSumInSequence(inventory[0]-newFufilledOrders+1, newFufilledOrders)

            inventory[0] = inventory[0]-newFufilledOrders
            totalCost += cost

        } else if (inventory.length > 1) {

            const ordersCanFulfill = inventory[0]

            const newFufilledOrders = Math.min(fulfilledOrders, ordersCanFulfill, 1)

            fulfilledOrders -= newFufilledOrders

