class Solution {
public:
    int maxProfit(vector<int>& A, int orders) {
        sort(begin(A), end(A));
        int mod = 1000000000 + 7;
        int ans = 0;
        int cnt = 0;
        int n = A.size();
        for(int i = n-1; i >= 0; i--){
            int H;
            if(i == 0){
                H = A[i];
            }
            else H = A[i] - A[i-1];
            int left = orders - cnt;
            if(H*(n-i) >= left){
                int D = left/(n-i);
                int R = left%(n-i);
                long long tot = (long long)A[i]*(A[i]+1)/2;
                long long minus = (long long)(A[i]-D)*(A[i]-D+1)/2;
                ans = (ans + (tot - minus)*(n-i)) % mod;
                ans = (ans + (long long)(A[i] - D)*R) % mod;
                break;
            }
            else{
                long long tot = (long long)A[i]*(A[i]+1)/2;
                long long minus = (long long)(A[i]-H)*(A[i]-H+1)/2;
                ans = (ans + (tot - minus)*(n-i)) % mod;
                cnt += H*(n-i);
            }
        }
        return ans;
    }
};
