class Solution {
public:
    typedef long long LL;
    const int mod = 1e9 + 7;
    bool check(int mid, int orders, vector<int>& arr) {
        LL res = 0;
        for(auto &t : arr) {
            if(t >= mid) {
                res += t-mid + 1;
            }    
        }
        return res >= orders;
    }
    int maxProfit(vector<int>& arr, int orders) {
        int l = 0, r = 1e9;
        while(l < r) {
            int mid =( l + r + 1) >> 1;
            if(check(mid, orders, arr)) l = mid;
            else r = mid - 1;
        }

        LL ans = 0;
        int price = l+1;
        for(auto &t : arr) {
            if(!orders) break;
            if(t >= price) {
                LL cnt = min(orders, t-price+1);
                orders -= cnt;
                ans = (ans + (LL)(cnt)*((LL)t+t-cnt+1)/2) % mod;
            } 
        }
        if(orders) ans = (ans + (LL)orders * l) % mod;
        return ans;
    }
};
