class Solution {
public:
    vector<int> decrypt(vector<int>& code, int k) {
        int n=code.size();

        if(k==0)
        {
            return vector<int>(n);
        }

        int sum=0;
        int i=0;

        if(k>0)
        {
            vector<int> next_sum(n);

            for(i=0;i<k;i++)
            {
                sum+=code[i];
            }

            for(int j=0;j<n;j++)
            {
                sum=sum-code[j]+code[i];
                next_sum[j]= sum;
                i=(i+1) % n;
            }

            return next_sum;

        }

         // if k<0

        k=abs(k);

        vector<int> pre_sum(n);
        sum=0;
        i=n-1;

        for(i=n-1;i>n-1-k;i--)
        {
            sum+=code[i];
        }

        for(int j=n-1;j>=0;j--)
        {
            sum=sum-code[j]+code[i];
            pre_sum[j]=sum;
            i=i-1;
            if(i<0) { i=n-1; }
        }

        return pre_sum;


    }
};
