class Solution {
public:
    vector<int> decrypt(vector<int>& a, int k) {
        int n = a.size(), s = 0;
        vector<int> prefix(n+1, 0);
        vector<int> ans(n, 0);
        // create prefix array 
        for(int i=0; i<n; i++) {
            prefix[i+1] = prefix[i] + a[i];
        }

        if(k > 0) {
            for(int i=0; i<n; i++) {
				// first part of the window 
                int r = min(n-1, i+k);
                ans[i] += prefix[r+1] - prefix[i+1];
				// second part of the window 
                r = k - (r - i);
                ans[i] += prefix[r];
            }
        } else if(k < 0) {
            k = -k;
            for(int i=0; i<n; i++) {
                int l = max(0, i-k);
				// first part of the window 
                ans[i] += prefix[i] - prefix[l];
                l = k - (i - l);
				// second part of the window 
                ans[i] += prefix[n] - prefix[n-l]; 
            }
        }

        return ans;
    }
};
