vector<int> decrypt(vector<int>& code, int k) {
        int n = code.size();
        vector<int> ans(n);
        int runsum = 0;
        bool rev = 0;

        if (k < 0) {
            rev = 1;
            k = -k;
            reverse(code.begin(), code.end());
        }

        for (int i = 0; i <= k; i++)
            runsum += code[i];

        for (int i = 0; i < n; i++) {
			runsum -= code[i];
            ans[i] = runsum;
            runsum += code[(i + k + 1) % n];
        }
        if (rev)
			reverse(ans.begin(), ans.end());

        return ans;
    }
