class Solution {
    public int sumg(int code[],int i,int k){//Function to find sum in case of k>0
        int s=0;i=i+1;
        while(k>0){
            if(i>code.length-1){
                 i=0;
                  s+=code[i];
            }
            else{
                s+=code[i];
            }
            i+=1;
            k--;
        }
        return s;
    }
    public int suml(int code[],int i,int k){//Function to find sum in case of k<0
       int s=0;i=i-1;
       while(k>0){
           if(i>=0&&i<code.length){
               s+=code[i];
           }
           else{
               i=code.length-1;
               s+=code[i];
           }
           i-=1;
           k-=1;
       }
       return s;
