class Solution {
public:
    vector<int> decrypt(vector<int>& a, int k) {
        int n=a.size(), j=0;
        vector<int> v(2*n, 0);
        vector<int> ret(n, 0);
        for(int i=0; i<n; ++i) v[i]=a[i];
        for(int i=n; i<2*n; ++i) v[i]=a[j++];
        if(k > 0) {
            for(int i=0; i<n; ++i) {
                int sum=0;
                for(int j=i+1; j<=i+k; ++j) {
                    sum+=v[j];
                }
                ret[i]=sum;
            }
        } else if(k < 0) {
            k=abs(k);
            for(int i=0; i<n; ++i) {
                int sum=0;
                for(int j=n+i-1; j>(n+i-1)-k; --j) {
                    sum+=v[j];
                }
                ret[i]=sum;
            }

        }
        return ret;
    }
};
