class Solution {
    func decrypt(_ code: [Int], _ k: Int) -> [Int] {
        var answer:[Int] = []
        let codeExt = code + code + code
        let count = code.count

        if k == 0 {
            answer = Array(repeating: 0, count: count)
        } else if k > 0{
            for i in 0..<count{
                let add = codeExt[count+1+i...count+i+k]
                answer.append(add.reduce(0) {$0 + $1})
            }
        } else {
            for i in 0..<count{
                let add = codeExt[count+k+i...count+i-1]
                answer.append(add.reduce(0) {$0 + $1})
            }
        }
        return answer
    }
}
```

