class Solution {
    public int[] decrypt(int[] code, int k) {
        int[] arr = new int[code.length];
        if (k == 0) {
            for (int i = 0; i < code.length; i++) {
                arr[i] = 0;
                return arr;
            }
        }
        int sum = 0;
        int ops;
        if (k > 0) {
            for (int i = 0; i < code.length; i++) {
                ops = k;
                int index = i + 1 <= code.length - 1 ? i + 1 : 0;
                int negative = 0;
                while (ops > 0) {
                    if (index <= code.length - 1) {
                        sum += code[index++];
                    } else {
                        sum += code[negative++];
                    }
                    ops--;
                }
                arr[i] = sum;
                sum = 0;
            }
        } else {
            for (int i = 0; i < code.length; i++) {
                ops = k;
                int index = i > 0 ? i - 1 : code.length - 1;
                int negative = code.length - 1;
                while (ops < 0) {
                    if (index >= 0) {
                        sum += code[index--];
                    } else {
                        sum += code[negative--];
                    }
                    ops++;
                }
                arr[i] = sum;
                sum = 0;
            }
        }
        return arr;        
    }
}
