var decrypt = function(code, k) {
    len = code.length
    arr = []

    for (let i = 0; i < len; i++) {        
        arr.push(scan(i))
    }

    return arr

    function scan(i) {        
        sum = 0
        s = k

        if (s == 0) return 0

        while (s > 0) {
            n = i + s
            if (n >= len) n = n - len             
            sum += code[n]
            s --
        }

        while (s < 0) {
            n = i + s    
            sum += code.at(n)
            s ++
        }

        return sum
    }
};
