public int minimumDeletions(String s) {
    int a = 0, b = 0;
    for(char c : s.toCharArray()) {
        if (c == 'a') a++;
        else b++;
    }
    int cA = 0, cB = 0, min = 0, delAll = a;
    for(int i = 0; i < s.length(); i++) {
        char c = s.charAt(i);
        if (a - cA == 0) break;
        if (c == 'a') {
            cA++;
        } else {
            cB++;
            delAll = Math.min(a - cA + min, delAll);
            min++;
        }
    }
    return Math.min(delAll, min);;
}
