class Solution {

    int lis(vector<int> & nums, int i, int p, vector<vector<int>> & dp) {

        int n = nums.size();

        if(i >= n)
            return 0;

        if(dp[i][p] != -1)
            return dp[i][p];

        int res = 0;

        if(nums[i] >= p)
            res = max(res, 1 + lis(nums, i + 1, nums[i], dp));

        res = max(res, lis(nums, i + 1, p, dp));

        dp[i][p] = res;

        return res;
    }

    int lengthOfLIS(vector<int> & nums) {

        int n = nums.size();

        int uniqueNums = 2;

        vector<vector<int>> dp(n + 1, vector<int>(uniqueNums, -1));

        return lis(nums, 0, 0, dp);
    }

public:
    int minimumDeletions(string s) {
        vector<int> nums;

        for(auto c : s) {
            if(c == 'a')
                nums.push_back(0);
            else 
                nums.push_back(1);
        }

        return s.length() - lengthOfLIS(nums);
    }
};
```

