class Solution {
public:
    // let endA[i] be the length of the longest balanced string up to position i and ending with 'a'
    // let endB[i] be the length of the longest balanced string up to position i and ending with 'b'
    //
    // now, consider position i+1
    //
    // if s[i+1] == 'a', we can form a longer balanced string by appending 'a'
	// to the the longest balanced string ending with 'a' up to position i,
    // thus
    //    endA[i+1] = endA[i] + 1
    // we cannot append 'a' to a string ending with 'b' to form a balance string so endB stays the same
    //    endB[i+1] = endB[i]
	//
    // if s[i+1] == 'b', we can form a longer balanced string by appending 'b'
	// to either the longest blanced string ending with 'b' or 'a' up to position i,
    // thus
    //    endB[i+1] = max(endB[i], endA[i]) + 1
    // endA stays the same
    //    endA[i+1] = endA[i]
    //
    // answer will be s.size() - max(endA[n], endB[n]);

    int minimumDeletions(string s) {
        std::ios_base::sync_with_stdio(0);
        std::cin.tie(0);

        int endA = 0, endB = 0;

        for (int i = 0; i < s.size(); ++i) {
            if (s[i] == 'a') {
                endA = endA + 1;
            } else {
                endB = std::max(endB + 1, endA + 1);
            }
        }

        return s.size() - std::max(endA, endB);
    }
};
