        while (!q.isEmpty()) {
            for (int i = q.size(); i > 0; i--) {
                int[] p = q.poll();
                if (p[0] == x) return ret;
                int idx = p[0] - b;
                if (idx >= 0 && !visited[idx][1] && p[1] == 0) {
                    q.add(new int[]{idx, 1});
                    visited[idx][1] = true; // have to make it visited when you put it into the queue
                }
                idx = p[0] + a;
                if (!visited[idx][0] && idx <= 4000) { // position beyond 4000 will never reach x because of rule 3
                    q.add(new int[]{idx, 0});
                    visited[idx][0] = true;
                }
            }
            ret++;
        }
        return -1;
    }
