        queue.add(new int[]{0,0,0});

        boolean[][] visited = new boolean[4000][2];
        for(int i:forbidden){
          visited[i][0] = true;
          visited[i][1] = true;
        }

        while(!queue.isEmpty()){
          int curr[] = queue.remove();
          int pos = curr[0];
          int dir = curr[1];
          int level = curr[2];
          if(pos==x)
            return level;
          if(visited[pos][dir])
            continue;
          visited[pos][dir] = true;

          if(pos+a<4000)
            queue.add(new int[]{pos+a,0,level+1});

          if(pos-b>-1 && dir==0)
            queue.add(new int[]{pos-b,1,level+1});
        }

        return -1;      
    }
}
