class Solution {
public:
    int minimumJumps(vector<int>& forbidden, int a, int b, int x) {
        node head = {0, false, 0};
        queue<node> q;
        q.push(head);
        bool visited[2001+2*2000] = {false};
        bool forbiddenList[2001+2*2000] = {false};
        int ret = -1;

        for(auto c: forbidden)
        {
            forbiddenList[c] = true;
        }
        int backward = 0;
        int forward  = 0;
        while(q.empty() == false)
        {
            head = q.front();
            q.pop();

            if(head.pos == x) /* bingo */
            {
                ret = head.jump;
                break;
            }
            if(visited[head.pos] == true)
                continue;
            if(head.backflag == false) //only set to visited when it isn't backward node;
                visited[head.pos] = true;

            forward  = head.pos + a; //go forward;
            backward = head.pos - b; //back;

            //handle the forward pos;
            if( (forward < 2000 + 2*2000 + 1)     && \
                (forbiddenList[forward] == false) && \
                (visited[forward] == false)/* cut the unnecessary path */ )
            {
                q.push({forward, false, head.jump+1});
            }

            //handle the backward pos;
            if( (backward >0)                      && \
                (forbiddenList[backward] == false) && \
                (head.backflag == false)           && \
                (visited[backward] == false) /* cut the unnecessary path */)
            {
                 q.push({backward, true, head.jump+1});
            }
        }
        return ret;
    }

};
