			let nb = curr - b;

			if (nb >= 0 && time <= 1 && !setb.has(nb) && !forbidden.has(nb)) {
				if (nb === x) return step+1;
				queue.push([nb, time+1]);
				setb.add(nb);
			}

		}
		step++;
	}

	return -1;
};

