```javascript []
/**
 * @param {number[]} forbidden
 * @param {number} a
 * @param {number} b
 * @param {number} x
 * @return {number}
 */
var minimumJumps = function(forbidden, a, b, x) {
    let queue = [];
    const seen = new Set();
    const banned = new Set(forbidden);

    queue.push([0, true])
    const maxPosition = a + b + Math.max(x, Math.max(...forbidden));
    let jumps = 0

    while (queue.length) {
        let size = queue.length

        while (size--) {
            let [cur, canGoBack] = queue.shift();

            if (cur === x)
                return jumps;

            const forward = cur + a, backward = cur - b;
            const fkey = `${forward}-true`, bkey = `${backward}-false`;

            if (!banned.has(forward) && forward <= maxPosition && !seen.has(fkey)) {
                queue.push([forward, true]);
                seen.add(fkey);
            }

            if (canGoBack && !banned.has(backward) && backward >= 0 && !seen.has(bkey)) {
                queue.push([backward, false]);
                seen.add(bkey);
            }
        }

        jumps++;
    }

    return -1
};
```
